import React, { useState, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { 
  ShoppingCart, 
  Search, 
  Menu, 
  User, 
  Package, 
  LogOut,
  MapPin,
  ChevronDown
} from 'lucide-react';
import { Dialog, DialogContent, DialogTrigger } from '@/components/ui/dialog';
import { Alert, AlertDescription } from '../ui';

import { useAuth } from '../contexts/AuthContext';

const Header = () => {
  const { isAuthenticated, user, logout } = useAuth();
  const navigate = useNavigate();
  const [location, setLocation] = useState('Select Location');
  const [isLocationDropdownOpen, setIsLocationDropdownOpen] = useState(false);
  const [isProfileDropdownOpen, setIsProfileDropdownOpen] = useState(false);
  const [showLoginDialog, setShowLoginDialog] = useState(false);
  const [showRegisterDialog, setShowRegisterDialog] = useState(false);

  const handleLocationDetect = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          try {
            const response = await fetch(
              `https://api.opencagedata.com/geocode/v1/json?q=${position.coords.latitude}+${position.coords.longitude}&key=YOUR_API_KEY`
            );
            const data = await response.json();
            if (data.results[0]) {
              setLocation(data.results[0].formatted);
            }
          } catch (error) {
            console.error('Error fetching location:', error);
          }
          setIsLocationDropdownOpen(false);
        },
        (error) => {
          console.error('Error getting location:', error);
        }
      );
    }
  };

  const handleLogout = () => {
    logout();
    setIsProfileDropdownOpen(false);
    navigate('/');
  };

  const UserMenu = () => (
    <div className="relative">
      <button
        onClick={() => setIsProfileDropdownOpen(!isProfileDropdownOpen)}
        className="p-2 hover:bg-gray-100 rounded-full flex items-center space-x-2"
      >
        <User className="h-5 w-5" />
        <span className="hidden md:inline text-sm">
          {user?.name || 'Account'}
        </span>
      </button>
      
      {isProfileDropdownOpen && (
        <div className="absolute top-full right-0 mt-2 w-56 bg-white rounded-lg shadow-lg py-2 z-50 border">
          {isAuthenticated ? (
            <>
              <div className="px-4 py-2 border-b">
                <div className="text-sm font-medium">Hello, {user?.name}</div>
                <div className="text-xs text-gray-500">{user?.email}</div>
              </div>
              <Link 
                to="/profile" 
                className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-blue-50 block"
              >
                Profile
              </Link>
              <Link 
                to="/orders" 
                className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-blue-50 flex items-center"
              >
                <Package className="h-4 w-4 mr-2" />
                Orders & Returns
              </Link>
              <div className="border-t my-1"></div>
              <button 
                onClick={handleLogout}
                className="w-full text-left px-4 py-2 text-sm text-red-600 hover:bg-red-50 flex items-center"
              >
                <LogOut className="h-4 w-4 mr-2" />
                Sign Out
              </button>
            </>
          ) : (
            <>
              <div className="px-4 py-2 border-b">
                <div className="text-sm font-medium">Welcome!</div>
                <div className="text-xs text-gray-500">Sign in for the best experience</div>
              </div>
              <button
                onClick={() => {
                  setIsProfileDropdownOpen(false);
                  setShowLoginDialog(true);
                }}
                className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-blue-50"
              >
                Sign In
              </button>
              <button
                onClick={() => {
                  setIsProfileDropdownOpen(false);
                  setShowRegisterDialog(true);
                }}
                className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-blue-50"
              >
                Register
              </button>
            </>
          )}
        </div>
      )}
    </div>
  );

  return (
    <header className="w-full bg-white shadow-sm sticky top-0 z-50">
      <div className="container mx-auto px-4 py-4">
        <div className="flex items-center justify-between">
          {/* Logo and Location */}
          <div className="flex items-center space-x-8">
            <Link to="/" className="flex items-center space-x-2">
              <h1 className="text-2xl font-bold text-blue-600">ValueCart</h1>
              <span className="bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded-full">Premium</span>
            </Link>
            
            {/* Location Dropdown */}
            <div className="relative">
              <button 
                className="flex items-center space-x-2 text-gray-600 hover:text-gray-800 bg-gray-50 px-3 py-2 rounded-full transition-colors"
                onClick={() => setIsLocationDropdownOpen(!isLocationDropdownOpen)}
              >
                <MapPin className="h-4 w-4" />
                <span className="max-w-[150px] truncate">{location}</span>
                <ChevronDown className="h-4 w-4" />
              </button>
              
              {isLocationDropdownOpen && (
                <div className="absolute top-full left-0 mt-2 w-64 bg-white rounded-lg shadow-lg py-2 z-50 border">
                  <button
                    className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-blue-50 flex items-center space-x-2"
                    onClick={handleLocationDetect}
                  >
                    <MapPin className="h-4 w-4 text-blue-600" />
                    <span>Detect Current Location</span>
                  </button>
                  <div className="border-t my-2"></div>
                  <div className="px-4 py-1 text-xs text-gray-500">POPULAR CITIES</div>
                  {['Mumbai', 'Delhi', 'Bangalore', 'Chennai'].map(city => (
                    <button
                      key={city}
                      className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-blue-50"
                      onClick={() => {
                        setLocation(city);
                        setIsLocationDropdownOpen(false);
                      }}
                    >
                      {city}
                    </button>
                  ))}
                </div>
              )}
            </div>
          </div>

          {/* Search Bar */}
          <div className="hidden md:flex flex-1 max-w-2xl mx-8">
            <div className="relative w-full">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
              <input
                type="text"
                placeholder="Search for products, brands and more..."
                className="w-full pl-10 pr-4 py-2.5 border rounded-full focus:outline-none focus:ring-2 focus:ring-blue-500 bg-gray-50"
              />
            </div>
          </div>

          {/* Navigation and Actions */}
          <div className="flex items-center space-x-6">
            <UserMenu />

            {/* Cart */}
            <Link to="/cart" className="p-2 hover:bg-gray-100 rounded-full relative flex items-center space-x-2">
              <div className="relative">
                <ShoppingCart className="h-5 w-5" />
                <span className="absolute -top-2 -right-2 bg-blue-600 text-white text-xs rounded-full h-4 w-4 flex items-center justify-center">
                  0
                </span>
              </div>
              <span className="hidden md:inline text-sm">Cart</span>
            </Link>

            {/* Mobile Menu */}
            <button className="p-2 hover:bg-gray-100 rounded-full md:hidden">
              <Menu className="h-5 w-5" />
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Search */}
      <div className="md:hidden px-4 pb-4">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
          <input
            type="text"
            placeholder="Search products..."
            className="w-full pl-10 pr-4 py-2.5 border rounded-full focus:outline-none focus:ring-2 focus:ring-blue-500 bg-gray-50"
          />
        </div>
      </div>

      {/* Auth Dialogs */}
      <Dialog open={showLoginDialog} onOpenChange={setShowLoginDialog}>
        <DialogContent>
          <LoginForm onClose={() => setShowLoginDialog(false)} />
        </DialogContent>
      </Dialog>

      <Dialog open={showRegisterDialog} onOpenChange={setShowRegisterDialog}>
        <DialogContent>
          <RegisterForm onClose={() => setShowRegisterDialog(false)} />
        </DialogContent>
      </Dialog>
    </header>
  );
};

export default Header;
