import React, { useState } from 'react';
import { ShoppingCart, Heart, Share2, Star } from 'lucide-react';
import { Alert, AlertDescription } from '../ui/alert';

const ProductCard = ({ 
  product, 
  isEmergency = false, 
  onAddToCart,
  className = ''
}) => {
  const [isWishlisted, setIsWishlisted] = useState(false);
  const [showAlert, setShowAlert] = useState(false);

  const handleAddToCart = () => {
    onAddToCart(product);
    setShowAlert(true);
    setTimeout(() => setShowAlert(false), 3000);
  };

  const handleWishlistToggle = () => setIsWishlisted(prev => !prev);

  const handleShare = async () => {
    try {
      await navigator.share({
        title: product.name,
        text: product.description,
        url: window.location.href,
      });
    } catch (err) {
      console.error('Error sharing product:', err);
    }
  };

  const discount = product.originalPrice ? Math.round((1 - product.price / product.originalPrice) * 100) : 0;

  return (
    <div className={`relative group rounded-xl bg-white shadow-lg hover:shadow-xl transition-all duration-300 ${className}`}>
      {showAlert && (
        <Alert className="absolute top-0 right-0 z-50 w-64 bg-green-50">
          <AlertDescription>Added to cart successfully!</AlertDescription>
        </Alert>
      )}
      
      {discount > 0 && (
        <div className="absolute top-4 left-4 bg-red-500 text-white px-2 py-1 rounded-full text-sm">
          -{discount}%
        </div>
      )}

      {isEmergency && (
        <div className="absolute top-4 right-4 bg-red-600 text-white px-3 py-1 rounded-full text-sm font-semibold">
          EMERGENCY
        </div>
      )}

      <img
        src={product.image}
        alt={product.name}
        className="w-full h-64 object-cover rounded-t-xl transform group-hover:scale-105 transition-transform duration-300"
      />

      <div className="absolute top-4 right-4 flex flex-col gap-2 opacity-0 group-hover:opacity-100 transition-opacity">
        <button onClick={handleWishlistToggle} className="p-2 bg-white rounded-full shadow-md hover:bg-gray-50 transition-colors">
          <Heart className={`w-5 h-5 ${isWishlisted ? 'fill-red-500 text-red-500' : 'text-gray-600'}`} />
        </button>
        <button onClick={handleShare} className="p-2 bg-white rounded-full shadow-md hover:bg-gray-50 transition-colors">
          <Share2 className="w-5 h-5 text-gray-600" />
        </button>
      </div>

      <div className="p-4">
        <h3 className="text-lg font-semibold text-gray-800 line-clamp-2">{product.name}</h3>
        
        <div className="flex justify-between items-start mb-2">
          <div className="flex items-center gap-1">
            <Star className="w-4 h-4 fill-yellow-400 text-yellow-400" />
            <span className="text-sm text-gray-600">{product.rating} ({product.reviewCount})</span>
          </div>
          
          <span className={`text-sm ${product.stockStatus === 'In Stock' ? 'text-green-600' : 'text-red-600'}`}>
            {product.stockStatus}
          </span>
        </div>

        <p className="text-sm text-gray-600 mb-3 line-clamp-2">{product.description}</p>

        <div className="flex justify-between items-center mb-3">
          <span className="text-xl font-bold text-gray-900">${product.price.toFixed(2)}</span>
          
          {product.originalPrice && (
            <span className="text-sm text-gray-500 line-through">${product.originalPrice.toFixed(2)}</span>
          )}
        </div>

        <button onClick={handleAddToCart} className={`flex items-center gap-2 px-4 py-2 rounded-lg text-white transition-colors ${isEmergency ? 'bg-red-600 hover:bg-red-700' : 'bg-blue-600 hover:bg-blue-700'}`}>
          <ShoppingCart className="w-4 h-4" />
          Add to Cart
        </button>

        {product.estimatedDelivery && (
          <span className="text-sm text-gray600">Delivery: {product.estimatedDelivery}</span>
        )}
      </div>
    </div>
  );
};

export default ProductCard;
