// src/services/api.jsx
import { useState, useEffect } from 'react';
import {
  loginApi,
  registerApi,
  getProducts,
  getProduct,
  addToCart,
  updateCartItem,
  removeFromCart,
  createOrder,
  getOrders,
  getUserProfile,
  updateUserProfile,
  getSubscriptionPlans,
  subscribe,
  cancelSubscription,
} from '';

// Hook for user authentication
export const useLogin = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const login = async (credentials) => {
    setLoading(true);
    try {
      const response = await loginApi(credentials);
      setLoading(false);
      return response.data;
    } catch (err) {
      setError(err);
      setLoading(false);
      return null;
    }
  };

  return { login, loading, error };
};

export const useRegister = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const register = async (userData) => {
    setLoading(true);
    try {
      const response = await registerApi(userData);
      setLoading(false);
      return response.data;
    } catch (err) {
      setError(err);
      setLoading(false);
      return null;
    }
  };

  return { register, loading, error };
};

// Hook to fetch products
export const useProducts = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await getProducts();
        setProducts(response.data);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  return { products, loading, error };
};

// Hook to fetch single product
export const useProduct = (id) => {
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await getProduct(id);
        setProduct(response.data);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    };
    fetchProduct();
  }, [id]);

  return { product, loading, error };
};

// Hook to manage cart actions
export const useCart = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const addToCartHandler = async (productId, quantity) => {
    setLoading(true);
    try {
      await addToCart(productId, quantity);
    } catch (err) {
      setError(err);
    } finally {
      setLoading(false);
    }
  };

  const updateCartItemHandler = async (itemId, quantity) => {
    setLoading(true);
    try {
      await updateCartItem(itemId, quantity);
    } catch (err) {
      setError(err);
    } finally {
      setLoading(false);
    }
  };

  const removeFromCartHandler = async (itemId) => {
    setLoading(true);
    try {
      await removeFromCart(itemId);
    } catch (err) {
      setError(err);
    } finally {
      setLoading(false);
    }
  };

  return {
    addToCart: addToCartHandler,
    updateCartItem: updateCartItemHandler,
    removeFromCart: removeFromCartHandler,
    loading,
    error,
  };
};

// Hook for user profile
export const useUserProfile = () => {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchUserProfile = async () => {
    setLoading(true);
    try {
      const response = await getUserProfile();
      setProfile(response.data);
    } catch (err) {
      setError(err);
    } finally {
      setLoading(false);
    }
  };

  const updateUserProfileHandler = async (userData) => {
    setLoading(true);
    try {
      await updateUserProfile(userData);
      await fetchUserProfile();
    } catch (err) {
      setError(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUserProfile();
  }, []);

  return { profile, updateUserProfile: updateUserProfileHandler, loading, error };
};

// Hook for subscription management
export const useSubscription = () => {
  const [plans, setPlans] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchPlans = async () => {
    setLoading(true);
    try {
      const response = await getSubscriptionPlans();
      setPlans(response.data);
    } catch (err) {
      setError(err);
    } finally {
      setLoading(false);
    }
  };

  const subscribeToPlan = async (planId) => {
    setLoading(true);
    try {
      await subscribe(planId);
    } catch (err) {
      setError(err);
    } finally {
      setLoading(false);
    }
  };

  const cancelSubscriptionHandler = async () => {
    setLoading(true);
    try {
      await cancelSubscription();
    } catch (err) {
      setError(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPlans();
  }, []);

  return {
    plans,
    subscribe: subscribeToPlan,
    cancelSubscription: cancelSubscriptionHandler,
    loading,
    error,
  };
};
