import React, { useState } from 'react';
import { 
  ShoppingCart, 
  Search, 
  Menu, 
  MapPin, 
  User, 
  Package, 
  Gift,
  ChevronDown,
  Calendar
} from 'lucide-react';

const Header = () => {
  const [location, setLocation] = useState('Select Location');
  const [isLocationDropdownOpen, setIsLocationDropdownOpen] = useState(false);
  const [isCategoryDropdownOpen, setIsCategoryDropdownOpen] = useState(false);
  const [isProfileDropdownOpen, setIsProfileDropdownOpen] = useState(false);
  
  const categories = {
    festive: {
      title: 'Festivals & Celebrations',
      items: [
        'Navratri Specials',
        'Birthday Celebrations',
        'Anniversary Gifts',
        'Party Essentials',
        'Festive & Occasion Needs'
      ]
    },
    grocery: {
      title: 'Daily Essentials',
      items: [
        'Vegetables & Fruits',
        'Dairy, Bread & Eggs',
        'Atta, Rice & Dal',
        'Oil & Ghee',
        'Masala & Spices'
      ]
    },
    gourmet: {
      title: 'Gourmet & Premium',
      items: [
        'Exotic & Premium Fruits',
        'Gourmet Cheese',
        'Organic & Hydroponic',
        'International Cuisine',
        'Premium Chocolates'
      ]
    },
    special: {
      title: 'Sweet Tooth',
      items: [
        'Ice Cream & Frozen Desserts',
        'Chocolates & Candies',
        'Cakes and Rolls',
        'Indian Sweets',
        'Bakery & Biscuits'
      ]
    }
  };

  const handleLocationDetect = () => {
    navigator.geolocation?.getCurrentPosition(
      position => {
        setLocation('Current Location');
        setIsLocationDropdownOpen(false);
      },
      error => {
        console.error('Location error:', error);
      }
    );
  };

  return (
    <header className="w-full bg-white shadow-sm sticky top-0 z-50">
      {/* Main header */}
      <div className="container mx-auto px-4 py-4">
        <div className="flex items-center justify-between">
          {/* Logo and Location */}
          <div className="flex items-center space-x-8">
            <div className="flex items-center space-x-2">
              <h1 className="text-2xl font-bold text-blue-600">ValueCart</h1>
              <span className="bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded-full">Premium</span>
            </div>
            
            {/* Location Dropdown */}
            <div className="relative">
              <button 
                className="flex items-center space-x-2 text-gray-600 hover:text-gray-800 bg-gray-50 px-3 py-2 rounded-full transition-colors"
                onClick={() => setIsLocationDropdownOpen(!isLocationDropdownOpen)}
              >
                <MapPin className="h-4 w-4" />
                <span className="max-w-[150px] truncate">{location}</span>
                <ChevronDown className="h-4 w-4" />
              </button>
              
              {isLocationDropdownOpen && (
                <div className="absolute top-full left-0 mt-2 w-64 bg-white rounded-lg shadow-lg py-2 z-50 border">
                  <button
                    className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-blue-50 flex items-center space-x-2"
                    onClick={handleLocationDetect}
                  >
                    <MapPin className="h-4 w-4 text-blue-600" />
                    <span>Detect Current Location</span>
                  </button>
                  <div className="border-t my-2"></div>
                  <div className="px-4 py-1 text-xs text-gray-500">POPULAR CITIES</div>
                  <button
                    className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-blue-50"
                    onClick={() => {
                      setLocation('Mumbai');
                      setIsLocationDropdownOpen(false);
                    }}
                  >
                    Mumbai
                  </button>
                  <button
                    className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-blue-50"
                    onClick={() => {
                      setLocation('Delhi');
                      setIsLocationDropdownOpen(false);
                    }}
                  >
                    Delhi
                  </button>
                </div>
              )}
            </div>
          </div>

          {/* Search Bar */}
          <div className="hidden md:flex flex-1 max-w-2xl mx-8">
            <div className="relative w-full">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
              <input
                type="text"
                placeholder="Search for products, brands and more..."
                className="w-full pl-10 pr-4 py-2.5 border rounded-full focus:outline-none focus:ring-2 focus:ring-blue-500 bg-gray-50"
              />
            </div>
          </div>

          {/* Navigation and Actions */}
          <div className="flex items-center space-x-6">
            {/* Categories Dropdown */}
            <div className="hidden md:block relative group">
              <button
                className="flex items-center space-x-2 hover:text-blue-600 group-hover:text-blue-600"
                onClick={() => setIsCategoryDropdownOpen(!isCategoryDropdownOpen)}
              >
                <span>Categories</span>
                <ChevronDown className="h-4 w-4" />
              </button>
              
              {isCategoryDropdownOpen && (
                <div className="absolute top-full right-0 mt-2 w-80 bg-white rounded-lg shadow-lg py-3 z-50 border">
                  <div className="grid grid-cols-1 gap-1">
                    {Object.entries(categories).map(([key, section]) => (
                      <div key={key} className="px-3">
                        <div className="flex items-center space-x-2 py-2">
                          {key === 'festive' && <Gift className="h-4 w-4 text-red-500" />}
                          {key === 'grocery' && <Package className="h-4 w-4 text-green-500" />}
                          {key === 'gourmet' && <Calendar className="h-4 w-4 text-purple-500" />}
                          <span className="font-medium text-sm">{section.title}</span>
                        </div>
                        <div className="pl-6 pb-3">
                          {section.items.map((item) => (
                            <button
                              key={item}
                              className="w-full text-left py-1.5 text-sm text-gray-600 hover:text-blue-600"
                            >
                              {item}
                            </button>
                          ))}
                        </div>
                        {key !== 'special' && <div className="border-b my-2"></div>}
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>

            {/* User Profile Dropdown */}
            <div className="relative">
              <button
                className="p-2 hover:bg-gray-100 rounded-full flex items-center space-x-2"
                onClick={() => setIsProfileDropdownOpen(!isProfileDropdownOpen)}
              >
                <User className="h-5 w-5" />
                <span className="hidden md:inline text-sm">Account</span>
              </button>
              
              {isProfileDropdownOpen && (
                <div className="absolute top-full right-0 mt-2 w-56 bg-white rounded-lg shadow-lg py-2 z-50 border">
                  <div className="px-4 py-2 border-b">
                    <div className="text-sm font-medium">Welcome!</div>
                    <div className="text-xs text-gray-500">Sign in for the best experience</div>
                  </div>
                  <button className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-blue-50">
                    Profile
                  </button>
                  <button className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-blue-50 flex items-center">
                    <Package className="h-4 w-4 mr-2" />
                    Orders & Returns
                  </button>
                  <button className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-blue-50">
                    Settings
                  </button>
                  <div className="border-t my-1"></div>
                  <button className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-blue-50">
                    Sign Out
                  </button>
                </div>
              )}
            </div>

            {/* Cart */}
            <button className="p-2 hover:bg-gray-100 rounded-full relative flex items-center space-x-2">
              <div className="relative">
                <ShoppingCart className="h-5 w-5" />
                <span className="absolute -top-2 -right-2 bg-blue-600 text-white text-xs rounded-full h-4 w-4 flex items-center justify-center">
                  0
                </span>
              </div>
              <span className="hidden md:inline text-sm">Cart</span>
            </button>

            {/* Mobile Menu */}
            <button className="p-2 hover:bg-gray-100 rounded-full md:hidden">
              <Menu className="h-5 w-5" />
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Search */}
      <div className="md:hidden px-4 pb-4">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
          <input
            type="text"
            placeholder="Search products..."
            className="w-full pl-10 pr-4 py-2.5 border rounded-full focus:outline-none focus:ring-2 focus:ring-blue-500 bg-gray-50"
          />
        </div>
      </div>
    </header>
  );
};

export default Header;
