const Header = () => {
  const { isAuthenticated } = useAuth();
  const [showAuthDialog, setShowAuthDialog] = useState(false);
  const [authView, setAuthView] = useState('login'); // login, register, or forgotPassword

  const handleCloseDialog = () => {
    setShowAuthDialog(false);
    setAuthView('login'); // Reset to login view when closing
  };

  const renderAuthContent = () => {
    switch (authView) {
      case 'register':
        return (
          <RegisterForm
            onClose={handleCloseDialog}
          />
        );
      case 'forgotPassword':
        return (
          <ForgotPasswordForm
            onBack={() => setAuthView('login')}
            onClose={handleCloseDialog}
          />
        );
      default:
        return (
          <LoginForm
            onClose={handleCloseDialog}
            onShowRegister={() => setAuthView('register')}
            onShowForgotPassword={() => setAuthView('forgotPassword')}
          />
        );
    }
  };

  return (
    <header className="bg-white shadow-sm">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo/Brand */}
          <div className="flex items-center">
            <span className="text-xl font-bold">Brand</span>
          </div>

          {/* Navigation */}
          <nav className="hidden md:flex items-center space-x-8">
            <a href="#" className="text-gray-700 hover:text-blue-600">
              Products
            </a>
            <a href="#" className="text-gray-700 hover:text-blue-600">
              Categories
            </a>
            <a href="#" className="text-gray-700 hover:text-blue-600">
              Deals
            </a>
          </nav>

          {/* Right Section */}
          <div className="flex items-center space-x-6">
            {/* Search */}
            <button className="text-gray-700 hover:text-blue-600">
              <Search className="h-5 w-5" />
            </button>

            {/* Cart */}
            <button className="text-gray-700 hover:text-blue-600 relative">
              <ShoppingCart className="h-5 w-5" />
              <span className="absolute -top-1 -right-1 bg-blue-600 text-white text-xs rounded-full h-4 w-4 flex items-center justify-center">
                0
              </span>
            </button>

            {/* User Menu / Auth */}
            {isAuthenticated ? (
              <UserMenu />
            ) : (
              <Dialog open={showAuthDialog} onOpenChange={setShowAuthDialog}>
                <DialogTrigger asChild>
                  <button className="text-gray-700 hover:text-blue-600 flex items-center space-x-1">
                    <User className="h-5 w-5" />
                    <span>Login</span>
                  </button>
                </DialogTrigger>
                <DialogContent>
                  {renderAuthContent()}
                </DialogContent>
              </Dialog>
            )}

            {/* Mobile Menu */}
            <button className="md:hidden text-gray-700 hover:text-blue-600">
              <Menu className="h-6 w-6" />
            </button>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
